package com.ncq.devstudio.workflows.controllers.test;

import com.ncq.devstudio.workflows.Application;
import com.ncq.devstudio.workflows.beans.NcqCategory;
import com.ncq.devstudio.workflows.beans.NcqWorkflow;
import com.ncq.devstudio.workflows.beans.Response;
import com.ncq.devstudio.workflows.beans.WorkflowsRequest;

import java.util.ArrayList;
import java.util.List;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertNotNull;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.boot.test.context.SpringBootTest.WebEnvironment;
import org.springframework.core.ParameterizedTypeReference;
import org.springframework.http.HttpEntity;
import org.springframework.http.HttpMethod;
import org.springframework.http.ResponseEntity;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.web.client.RestTemplate;

/**
 * Class de test de l'API REST
 *
 * @author Aroua SOUABNI
 *
 */
@RunWith(SpringJUnit4ClassRunner.class)
@SpringBootTest(classes = Application.class, webEnvironment = WebEnvironment.DEFINED_PORT)
public class WorkflowApi {

    protected RestTemplate restTemplate = new RestTemplate();
    protected static final String BASE_URL = "http://localhost:9011/wfapi/v1";

    @Test
    public void addCategoryAndWorkflowTest() {
        NcqCategory category = new NcqCategory();
        category.setDescription("desc");
        category.setEnabled(1);
        category.setLogo("");
        category.setLogoExtension("png");
        category.setName("category name");

        HttpEntity<NcqCategory> requestCat = new HttpEntity(category);

        ResponseEntity<Response<NcqCategory>> response
                = restTemplate.exchange(BASE_URL + "/category/add",
                        HttpMethod.POST, requestCat,
                        new ParameterizedTypeReference<Response<NcqCategory>>() {
                });

        assertNotNull(response.getBody().getObject());

        NcqWorkflow wf = new NcqWorkflow();
        List<NcqCategory> categories = new ArrayList();
        categories.add(response.getBody().getObject());
        wf.setCategories(categories);
        wf.setDescription("New workflow description. It contains one category.");
        wf.setEnabled(2);
        wf.setName("New workflow");
        HttpEntity<NcqWorkflow> requestWf = new HttpEntity(wf);

        ResponseEntity<Response<NcqWorkflow>> responseWf
                = restTemplate.exchange(BASE_URL + "/workflow/add",
                        HttpMethod.POST, requestWf,
                        new ParameterizedTypeReference<Response<NcqWorkflow>>() {
                });
        assertEquals(responseWf.getBody().getObject().getName(), "New workflow");
        
        
    }
    
    @Test
    public void filterWorkflowsTest() {

        WorkflowsRequest wr = new WorkflowsRequest();
        wr.setEnabled(2);
        wr.setName("New workflow");
        HttpEntity<WorkflowsRequest> requestWf = new HttpEntity(wr);

        ResponseEntity<Response<List<NcqWorkflow>>> responseWf
                = restTemplate.exchange(BASE_URL + "/workflow/filter/{page}",
                        HttpMethod.POST, requestWf,
                        new ParameterizedTypeReference<Response<List<NcqWorkflow>>>() {
                }, 0);
        assertFalse(responseWf.getBody().getObject().isEmpty());
        
        
    }
}
